import { handleActions, Action } from 'redux-actions';
import { getUserStart, getUserSuccess, getUserError, GetUserSuccessPayload } from './userActions';
import { loginSuccess, registerSuccess } from '../auth/authActions';
import * as appActions from '../app/appActions';
import { authActions } from '../auth';
import { UserType } from '../../types';


export interface UserStateType {
  isLoading: boolean
  isSuccess?: boolean
  isError?: boolean | null
  error?: boolean | null
  user: UserType | null
}

const INITIAL_STATE = {
    isLoading: false,
    isSuccess: false,
    isError: false,
    error: null,
    user: {
     data: null
  },
}

export default handleActions<UserStateType | GetUserSuccessPayload>({
    [getUserStart.toString()]: (state) => {
      console.log('STATE', state);
      return {
        ...state,            
          isLoading: true,
          isError: false,

      };
    },

    [getUserSuccess.toString()]: (state, { payload}) => {
      console.log("REDUCER", payload.user)
       return { 
         ...state,
        isLoading: false,
        isError: false,
        data: payload.user,
      }
    },

    [getUserError.toString()]: (state) => ({
        ...state,       
        isLoading: false,
        isError: true,    
    }),
  },
  INITIAL_STATE,
);
