import React, { useEffect, useState } from "react";
import { Box, Button, TextField, makeStyles, } from "@material-ui/core";
import { connect } from "react-redux";
import { Formik, Form } from "formik";
import { createEntry, getEntries } from '../../modules/entries/entriesOperation'
import FormField from "../Form/FromField"
import { Route,  useHistory } from "react-router-dom"
import EntriesItems from './EntriesItems'

const useStyles = makeStyles((theme) => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 200,
  },
}));

// interface BanksListPropsType {
//   get: any;
//   isLoading: boolean;
//   banks: Array<BankType>;
//   createBank:(bank: BankType) => Promise<boolean>;
//   updateBank: (bank: BankType) => Promise<boolean>;
//   deleteBank: (_id: string) => Promise<boolean>;
// }

const Entries: React.FC<{}> = ({ user, createEntry, getEntries }) => {
  const [date, setDate] = useState('2021-04-21')
  const classes = useStyles();
  const history = useHistory()

  // useEffect(() => {
  //   createEntry();
  // }, [createEntry]);

  // if (isLoading) {
  //   return <div>Loading ...</div>;
  // }
//  <Link to={`/entry/userId:${user.id}&date:${date}`}> </Link>
  useEffect(() => {
   history.push(`/e/${user.id}/${date}`)
  },[date, history, user.id])

  function handeleChange(e) {
    console.log(e.target.value)
    setDate(e.target.value)   
  }

  return (
    <Box>
       {/* <h4>Entries
         <Link to={`/e/${user.id}/${date}`}>entru</Link>
       </h4> */}
       <Box>
                <Formik
                    initialValues={{
                        catagorie: "",
                        amount: ""
                    }}
                    onSubmit={async (values, actions) => {

                       const red = await createEntry({
                         amount: values.amount,
                         catagorie: values.catagorie, 
                         userId: user.id,
                         date,
                       })
                        console.log('REsult', { 
                          values,
                          userId: user.id,
                          date,
                         } )

                    }}
                >   
                    {(props) => (
                    <Form>

                        <Box>

                        <TextField
                            id="date"
                            label="Birthday"
                            onChange={handeleChange}
                            value={date}
                            type="date"
                            className={classes.textField}
                            InputLabelProps={{
                            shrink: true,
                          }}
                        />
                        </Box>

                        <Box>
                            <FormField
                                {...props}
                                name="amount"
                                placeholder="Spent amount $"
                                label="amount"
                            />
                        </Box>
                        <Box>
                            <FormField
                                {...props}
                                name="catagorie"
                                placeholder="Catagory"
                                label="catagorie"
                            />
                        </Box>
                        <Button type="submit">
                              Create Entry
                        </Button>    
                    </Form>
                    )}
                </Formik>
            </Box>
    <Box>    
        <Route path={`/e/:userId/:date`}>
            <EntriesItems />    
        </Route>
    </Box>
    </Box>
  );
};
const mapStateToProps = (state) => {
  return {
    user: state.userReducer.data,
    //isLoading: state.banksReducer.isLoading,
  };
};

const mapDispatchToState = {
  createEntry,
  getEntries
};


export default connect(mapStateToProps, mapDispatchToState)(Entries);
