import { createStore, applyMiddleware, Store, CombinedState } from "redux";
import thunk from "redux-thunk";
import logger from "redux-logger";
import reducer from "../modules";
import { Action } from "redux-actions";
import { GetEntriesSuccessPayload } from "../modules/entries/entriesActions";
import { StateType } from "../modules/entries/entriesReducer";

export type MainStoreType = Store<
  CombinedState<{ banksReducer: StateType }>,
  Action<GetEntriesSuccessPayload>
>;

const store = createStore(reducer, applyMiddleware(thunk, logger));

export const useStore = () => store;

export default store;
