import { handleActions, Action, combineActions, CombinedActionType } from 'redux-actions'
import { getUserStart, getUserSuccess, getUserError, GetUserSuccessPayload  } from './userActions';
import { loginSuccess, registerSuccess } from '../auth/authActions';
import * as appActions from '../app/appActions';
import { authActions } from '../auth';
import { UserType, UserSingleType } from '../../types';


export interface UserStateType {
  isLoading: boolean
  isSuccess?: boolean
  isError?: boolean | null
  error?: boolean | null
  user: UserSingleType |null
}

const INITIAL_STATE = {
    isLoading: false,
    isSuccess: false,
    isError: false,
    error: null,
    user: null, 

}


export default handleActions<UserStateType, GetUserSuccessPayload>({
    [getUserStart.toString()]: (state): UserStateType => {
      console.log('STATE', state);
      return {
        ...state,            
          isLoading: true,
          isError: false,

      };
    }, 

