import React, { useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { useParams } from "react-router-dom";
import EntryItem from "./EntryItem";
import DeleteIcon from "@material-ui/icons/Delete";
import EditIcon from "@material-ui/icons/Edit";
import {
  Box,
  TableContainer,
  Paper,
  Table,
  TableBody,
  TableRow,
  TableCell,
  Button,
} from "@material-ui/core";
import { createData } from "../../utils/createData"
import './entries.scss'
const useStyles = makeStyles({
  table: {
    minWidth: 250,   
    borderCollapse: 'separate',
    borderSpacing: '10px'    
  }, 
})


const EntriesItems = ({ getEntries, entries, isLoading }) => {
  const classes = useStyles();
  const { userId, date } = useParams();
  let total: number = entries.reduce((acc, item) => {
    return acc + Number(item.amount);
  }, 0);

  const rows = entries.map((e) => createData(e.id, e.catagorie, e.amount));

  useEffect(() => {
    getEntries(userId, date);
  }, [getEntries, date, userId]);

  if (isLoading) {
    return <div>Loading ...</div>;
  }

  return (
    <div>
      <h2>Day spending: </h2>
      <TableContainer component={Paper}>
        <Table
          className={classes.table}
          size="small"
          aria-label="a dense table"
        >
          <TableBody>
            {rows.map((row) => (
               <EntryItem {...row} />

            ))}
          </TableBody>
        </Table>
      </TableContainer>
      {entries.map((item) => {
        return (
          <Box>
            <EntryItem {...item} />
          </Box>
        );
      })}
      <Box>Total: {total} </Box>
    </div>
  );
};

export default EntriesItems;
