import { createAction } from "redux-actions";
import { EntrieType } from "../../types";

const GET_ENTRIES_START = "GET_ENTRIES_START";
const GET_ENTRIES_SUCCESS = "GET_ENTRIES_SUCCESS";
const GET_ENTRIES_ERROR = "GET_ENTRIES_ERROR";

export type GetEntriesSuccessPayload = Array<EntrieType>;
export type CreateEntrieSuccessPayload = EntrieType;
export type UpdateEntrieSuccessPayload = EntrieType;
export type DeleteEntrieSuccessPayload = number;

export type CombinedPayloads =  GetEntriesSuccessPayload & CreateEntrieSuccessPayload  & UpdateEntrieSuccessPayload & DeleteEntrieSuccessPayload

export const getEntriesStart = createAction<void>(GET_ENTRIES_START);
export const getEntriesSuccess = createAction<GetEntriesSuccessPayload>(
  GET_ENTRIES_SUCCESS
);
export const getEntriesError = createAction<void>(GET_ENTRIES_ERROR);

export const createEntrieStart = createAction<void>("CREATE_BANK_START");
export const createEntrieSuccess = createAction<CreateEntrieSuccessPayload>(
  "CREATE_BANK_SUCCESS"
);
export const createEntrieError = createAction<void>("CREATE_ENTRIE_ERROR");

export const updateEntrieStart = createAction<void>("UPDATE_ENTRIE_START");
export const updateEntrieSuccess = createAction<UpdateEntrieSuccessPayload>(
  "UPDATE_BANK_SUCCESS"
);
export const updateEntrieError = createAction<void>("UPDATE_ENTRIE_ERROR");

export const deleteEntrieStart = createAction<void>("DELETE_ENTRIE_START");
export const deleteEntrieSuccess = createAction<DeleteEntrieSuccessPayload>(
  "DELETE_ENTRIE_SUCCESS"
);
export const deleteEntrieError = createAction<void>("DELETE_ENTRIE_ERROR");
