import {
  getEntriesStart,
  getEntriesSuccess,
  getEntriesError,
  createEntrieStart,
  createEntrieSuccess,
  createEntrieError,
  updateEntrieStart,
  updateEntrieSuccess,
  updateEntrieError,
  deleteEntrieStart,
  deleteEntrieSuccess,
  deleteEntrieError
} from "./entriesActions";
import { ApiEntries } from "../../api";
import { Dispatch } from "redux";
import { Action } from "redux-actions";
import { ThunkAction } from "redux-thunk";
import { StateType } from "./entriesReducer";
import { EntrieType } from "../../types";

type EntiesPayload = {
  payload: () => EntrieType[];
};

export type ThunkType = ThunkAction<
  Promise<Array<EntrieType> | void | EntrieType>,
  StateType,
  unknown,
  Action<EntiesPayload>
>;

export function getEntries(userId: number, date: string): ThunkType {
  return async function getEntriesThunk(
    dispatch: Dispatch<Action<void> | Action<Array<EntrieType>>>
  ) {
    try {
      dispatch(getEntriesStart());
      const entries = await ApiEntries.fetchEntries(userId, date);
      dispatch(getEntriesSuccess(entries.data));
    } catch (error) {
      dispatch(getEntriesError());
    }
  };
}

export function createEntrie(entrie: EntrieType): ThunkType {
  return async function createBankThunk(
    dispatch: Dispatch<Action<void> | Action<EntrieType>>,
    getState
  ) {
    try {
      dispatch(createEntrieStart());
      const data = await ApiEntries.createEntrie(entrie);

      dispatch(createEntrieSuccess(data));
    } catch (error) {
      dispatch(createEntrieError());
    }
  };
}

export function updateEntrie(entrie: EntrieType): ThunkType {
  return async function updateBankThunk(
    dispatch: Dispatch<Action<void> | Action<EntrieType>>
  ) {
    try {
      dispatch(updateEntrieStart());
      const data = await ApiEntries.updateEntrie(entrie);
      dispatch(updateEntrieSuccess(data));
    } catch (error) {
      dispatch(updateEntrieError());
    }
  };
}

export function deleteBank(id: number): ThunkType {
  return async function deleteBankThunk(
    dispatch: Dispatch<Action<number | void> | Action<EntrieType>>
  ) {
    try {
      dispatch(deleteEntrieStart());
        await ApiEntries.deleteEntries(id);

      dispatch(deleteEntrieSuccess(id))
    } catch (error) {
      dispatch(deleteEntrieError());
    }
  };
}
