import axios from 'axios'
import { EntrieType, UserType } from "../types"
import { useHistory } from 'react-router-dom'

type TokenType = string
type AuthDataType = {
    data: Array<UserType>
    token: string
}

type ApiAuthType = {
   data: AuthDataType
  [propName: string]:  any
}

export const ApiEntries = {    
    fetchEntries(userId: number): Promise<Array<EntrieType>> {
      return axios.get(`https://budget-track-elif.herokuapp.com/entries/get/?userId=${userId}`)
    },

    createEntrie( entrie: EntrieType): Promise<EntrieType> {
      return axios.post('https://budget-track-elif.herokuapp.com/entries/create', entrie)
    },

    updateEntrie( entrie: EntrieType): Promise<EntrieType> {
        return axios.put(`https://budget-track-elif.herokuapp.com/entries/update`, entrie)
    },

    deleteEntries(id: number): Promise<boolean> {
        return axios.delete(`https://budget-track-elif.herokuapp.com/entries/delete/?id=${id}`)
    }
}

export const ApiAuth = {
  _token: null,
  isLoggedIn() {
    return !!this._token;
  },
  _storeTokenToAxios(token: TokenType) {
    console.log("_storeTokenToAxios", token)
    axios.defaults.headers.common.Authorization = `Bearer ${token}`
    // axios.defaults.headers.common.Authorization = `Bearer ${this._token}`
  },

  setToken(token: any) {
    this._token = token
    this._storeTokenToAxios(token)
  },

  init() {
      try {
         const token = window.localStorage.getItem('token')
         this.setToken(token)
      } catch (error) {
        console.log('NOT FOUND TOKEN');
        console.log(error);

      }  
      return this._token
  },

  register(entrie: EntrieType): Promise<ApiAuthType> {
    return axios.post(`https://budget-track-elif.herokuapp.com/auth/register`, entrie )
  },

  login(entrie: EntrieType): Promise<ApiAuthType> {
    return axios.post(`https://budget-track-elif.herokuapp.com/auth/login`, entrie)
  },
}

export const ApiAccount = {
  get(): Promise<UserType> {
    return axios.get('ap/account')
  },
}
