import React, { useEffect, useState } from "react";
import { Box, Button, TextField, makeStyles } from "@material-ui/core";
import { connect } from "react-redux";
import { Formik, Form } from "formik";
import {
  createEntry,
  getEntries,
  updateEntry,
  deleteEntry,
} from "../../modules/entries/entriesOperation";
import FormField from "../Form/FromField";
import { Route, useHistory } from "react-router-dom";
import EntriesItems from "./EntriesList";

const useStyles = makeStyles((theme) => ({
  container: {
    display: "flex",
    flexWrap: "wrap",
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 200,
  },
}));

// interface BanksListPropsType {
//   get: any;
//   isLoading: boolean;
//   banks: Array<BankType>;
//   createBank:(bank: BankType) => Promise<boolean>;
//   updateBank: (bank: BankType) => Promise<boolean>;
//   deleteBank: (_id: string) => Promise<boolean>;
// }

const Entries: React.FC<{}> = ({
  user,
  createEntry,
  getEntries,
  updateEntry,
  deleteEntry,
  entries,
  isLoading,
}) => {
  const [date, setDate] = useState("2021-04-21");
  const [amount, setAmount] = useState("");
  const [catagorie, setCatagorie] = useState("");
  const classes = useStyles();
  const history = useHistory();
  const userId = user.id

  // useEffect(() => {
  //   createEntry();
  // }, [createEntry]);

  // if (isLoading) {
  //   return <div>Loading ...</div>;
  // }
  //  <Link to={`/entry/userId:${user.id}&date:${date}`}> </Link>
  useEffect(() => {
    history.push(`/e/${user.id}/${date}`);
  }, [date, history, user.id]);

  function handeleDate(e) {
    console.log(e.target.value);
    setDate(e.target.value);
  }
  function handeleAmount(e) {
    console.log(e.target.value);
    setAmount(e.target.value);
  }
  function handeleCatagorie(e) {
    console.log(e.target.value);
    setAmount(e.target.value);
  }

  return (
    <Box>
      <Box>
        <Formik
          initialValues={{
            catagorie: "",
            amount: "",
          }}
          onSubmit={async (values, actions) => {
            try {
              await createEntry({
                amount: values.amount,
                catagorie: values.catagorie,
                userId: user.id,
                date,
              });
              console.log("Values", values, date, user.id ,)
              actions.resetForm()
              // setAmount("");
              // setCatagorie("");
            } catch (error) {
              console.log("Error", error);
            }
          }}
        >
          {(props) => (
            <Form>
              <Box display="flex">
                <Box mr={15}>
                  <TextField
                    id="date"
                    label="Trecker"
                    onChange={handeleDate}
                    value={date}
                    type="date"
                    className={classes.textField}
                    InputLabelProps={{
                      shrink: true,
                    }}
                  />
                </Box>
                <Box display="flex" flexDirection="column" justifyContent="center" alignItems="center">  
                <Box mb={3}>
                  {/* <TextField                    
                    label="amount"
                    onChange={handeleAmount}
                    value={amount}  
                    type="number"            
                    placeholder="Spent Amount ($)"
                    className={classes.textField}
                    InputLabelProps={{
                      shrink: true,
                    }}
                  /> */}
                  <FormField
                      {...props}                          
                      name="amount"
                      placeholder="Spent amount $"
                      label="amount"
                  />
                </Box>

                <Box mb={3}>
                  {/* <TextField
                    label="Catagory"
                    onChange={handeleCatagorie}
                    value={catagorie}
                    placeholder="Catagory"
                    className={classes.textField}
                    InputLabelProps={{
                      shrink: true,
                    }}
                  /> */}
                  <FormField
                      {...props}                          
                      name="catagorie"
                      placeholder="Catagory"
                      label="catagorie"
                  />

                </Box>
                <Button type="submit">Create Entry</Button>
                </Box>  
              </Box>
            </Form>
          )}
        </Formik>
      </Box>
      <Box>
        <Route exact path={`/e/:userId/:date`}>
          <EntriesItems
            {...{userId}}
            getEntries={getEntries}
            updateEntry={updateEntry}
            {...{deleteEntry}}
            {...{ entries }}
            {...{ isLoading }}
          />
        </Route>
      </Box>
    </Box>
  );
};
const mapStateToProps = (state) => {
  return {
    user: state.userReducer.data,
    entries: state.entriesReducer.items,
    isLoading: state.entriesReducer.isLoading,
  };
};

const mapDispatchToState = {
  createEntry,
  getEntries,
  updateEntry,
  deleteEntry
};

export default connect(mapStateToProps, mapDispatchToState)(Entries);
