import { handleActions, combineActions } from 'redux-actions';
import { getUserStart, getUserSuccess, getUserError } from './userActions';
import { loginSuccess,registerSuccess } from '../auth/authActions';
import * as appActions from '../app/appActions';

const INITIAL_STATE = {
  fetchUser: {
    isLoading: false,
    isError: false,
    error: null,
  },
  user: null,
};

export default handleActions(
  {
    [getUserStart.toString()]: (state) => {
      console.log('STATE', state);
      return {
        ...state,
        fetchUser: {
          ...state.fetchUser,
          isLoading: true,
          isError: false,
        },
      };
    },

    // [combineActions(
    //   // authActions.login.success,
    //   loginSuccess.toString(),
    //   registerSuccess.toString(),
    //   getUserSuccess.toString(),
    // )]: (state, action) => ({
    //   ...state,
    //   fetchViewer: {
    //     ...state.fetchViewer,
    //     isLoading: false,
    //     isError: false,
    //   },
    //   user: action.payload,
    // }),

    [getUserError.toString()]: (state) => ({
      ...state,
      fetchViewer: {
        ...state.fetchUser,
        isLoading: false,
        isError: true,
        error: true,
      },
    }),
  },
  INITIAL_STATE,
);
