import DeleteIcon from "@material-ui/icons/Delete";
import EditIcon from "@material-ui/icons/Edit";
import CancelIcon from "@material-ui/icons/Cancel";
import SaveIcon from "@material-ui/icons/Save";
import green from "@material-ui/core/colors/green";
import React, { useState } from "react";
import {
  Box,
  Button,
  makeStyles,
  TableRow,
  TableCell,
  TextField,
} from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  button: {
    margin: theme.spacing(1),
  },
}));

const EntryItem = ({ id, catagorie, amount }) => {
  const [catagorieData, setCatagorieData] = useState(catagorie);
  const [amountData, setAmountData] = useState(amount);
  const [isEdit, setEdit] = useState(false);
  const classes = useStyles();

  function handleIsEdit() {
    setEdit(!isEdit);
  }

  function handleCategorie(e) {
   setCatagorieData(e.target.value)
  }

  function handleAmount(e) {
   setAmountData(e.target.value)
  }

  function handleSave() {}
  return (
    <TableRow key={catagorie}>
      {isEdit ? (
        <>
         <TextField
              id="standard-size-small"
              value={catagorieData ? catagorieData : ""}
              // error={mLoan === 0}
              helperText={ catagorieData === "" && "Empty input is not allowed"}
              type="text"
              // InputProps={{
              //   inputProps: { 
              //        min: 0,
              //        style: {
              //          textAlign: "center"
              //        } 
              //   }
              // }}
              // placeholder="max loan"
              size="small"
              rowsMax={4}
              onChange={handleCategorie}
            />
         <TextField
              id="standard-size-small"
              value={amountData ? amountData : ""}
              // error={mLoan === 0}
              helperText={ catagorieData === "" && "Empty input is not allowed"}
              type="number"
              // InputProps={{
              //   inputProps: { 
              //        min: 0,
              //        style: {
              //          textAlign: "center"
              //        } 
              //   }
              // }}
              // placeholder="max loan"
              size="small"
              rowsMax={4}
              onChange={handleAmount}
            />


        </>
      ) : (
        <>
          <TableCell component="th" scope="row">
            {catagorie}
          </TableCell>
          <TableCell align="right">{amount}</TableCell>

        </>
      )}
      <Button
            onClick={handleIsEdit}
            variant="contained"
            color="primary"
            className={classes.button}
            startIcon={<EditIcon />}
          >
            Edite
          </Button>
          <Button
            variant="contained"
            color="secondary"
            className={classes.button}
            startIcon={<DeleteIcon />}
          >
            Delete
          </Button>
    </TableRow>
  );
};

export default EntryItem;

