/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState } from "react";
import { Box } from "@material-ui/core";
import { Link, useHistory } from "react-router-dom";
import blue from "@material-ui/core/colors/blue";
import { connect } from "react-redux";
import "../scss/style.scss";
import { UserSingleType } from "../types";
import { getUser } from "../modules/user/userOperations";

type HeaderType = {
  user: UserSingleType;
  isLoading: boolean;
  getUser: any;
};

const Header: React.FC<HeaderType> = ({ user, isLoading, getUser }) => {
  const token = window.localStorage.getItem("token")
  const [isLogin, setIslogin] = useState(token)
  const history =  useHistory()

  if (isLoading) {
    return <div>Loading...</div>;
  }

  const handleLogout = () => {
    window.localStorage.removeItem("token");
    const token = window.localStorage.getItem("token");
    setIslogin(token);
    history.push('/')
  };

  const auth = (
    <Box display="flex" alignContent="center">
      <Link to="/register">
        <Box>Register</Box>
      </Link>
      <Box ml={4}>
        <Link to="/login">
          <Box>Login</Box>
        </Link>
      </Box>
    </Box>
  );

  return (
    <Box bgcolor={blue[500]} width="100%" height="150px" color="text.primary">
      <Box
        className="container"
        display="flex"
        alignItems="center"
        justifyContent="space-between"
      >
        <Box className="container__logo">
          <Link to="/">
            <img src="morimage.png" alt="Logo" />
          </Link>
        </Box>
        {isLogin ? (
          <Box display="flex" alignContent="center">
            <Box>{user.username}</Box>
            <Box ml={4} className="logout" onClick={handleLogout} style={{ cursor: "pointer" }}>
              Logout
            </Box>
          </Box>
        ) : (
          auth
        )}
      </Box>
    </Box>
  );
};
const mapDispatchToState = {
  getUser,
};
function mapStateToProps(state) {
  return {
    user: state.userReducer.user,
    isLoading: state.userReducer.isLoading,
  };
}

export default connect(mapStateToProps, mapDispatchToState)(Header);
