import React, { useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { useParams } from "react-router-dom";
import EntryItem from "./EntryItem";
import {
  Box,
  TableContainer,
  Paper,
  Table,
  TableBody,
} from "@material-ui/core";
import { createData } from "../../utils/createData";
import "./entries.scss";
import { EntrySingleType } from "../../types";

const useStyles = makeStyles({
  table: {
    minWidth: 250,
    borderCollapse: "separate",
    borderSpacing: "10px",
  },
});

type EntriesItemsType = {
  isLoading: boolean;
  entries: Array<EntrySingleType>;
};
const EntriesItems: React.FC<EntriesItemsType> = ({
  getEntries,
  updateEntry,
  deleteEntry,
  entries,
  isLoading,
}) => {
  const classes = useStyles();
  const { userId, date } = useParams();
  let total: number = entries.reduce((acc, item) => {
    return acc + Number(item.amount);
  }, 0);

  const rows = entries.map((e) =>
    createData(e.id, e.catagorie, e.amount, e.date)
  );

  useEffect(() => {
    getEntries(userId, date);
  }, [getEntries, date, userId]);

  if (isLoading) {
    return <div>Loading ...</div>;
  }

  return (
    <Box>
      <h2>Day spending: </h2>
      <TableContainer component={Paper} style={{ width: "60%" }}>
        <Table
          className={classes.table}
          size="small"
          aria-label="a dense table"
        >
          <TableBody>
            {rows.map((row) => (
              <EntryItem
                {...row}
                {...{ userId }}
                {...{ updateEntry }}
                {...{ deleteEntry }}
                {...{ isLoading }}
              />
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <Box display="flex" justifyContent="space-between" width={"20%"}>
        <span>Total:</span> <span>{total}</span>
      </Box>
    </Box>
  );
};

export default EntriesItems;
