import React from "react"
import Layout from "../components/Layout"
import { appOperations } from "../modules/app"
import Entries from "../components/Entries/Entries"
import {  Box } from "@material-ui/core"
import { connect } from "react-redux"
import { UserSingleType } from "../types"
import { MainStateType } from '../store/createState'

type MainType = {
  isLoading: boolean
  user: UserSingleType
};

class Main extends React.Component<MainType> {

  constructor(props) {
    super(props);
    console.log("props", props);
    props.dispatch(appOperations.init());
  }
 componentDidMount() {
  window.addEventListener('click', function(e: React.ChangeEvent<HTMLInputElement>) {    
    if(e.target.classList.contains("logout")) {
      history.push("/")      
    }    
  })
 }
  render() {
    const token = window.localStorage.getItem('token')
    if (this.props.isLoading) {
      return <div>Loading...</div>;
    }
    return (
      <Layout>
        <Box mb={5}>
          {token ? (
            <Box>
              <Entries />
            </Box>
          ) : (
            <Box>
              <img
                loading="lazy"
                src="https://cdn.corporatefinanceinstitute.com/assets/budget-1024x617.jpeg"
                alt="Budget"
                width="1024"
                height="617"
                //srcset="//cdn.corporatefinanceinstitute.com/assets/budget-1024x617.jpeg 1024w, //cdn.corporatefinanceinstitute.com/assets/budget-300x181.jpeg 300w, //cdn.corporatefinanceinstitute.com/assets/budget-768x463.jpeg 768w, //cdn.corporatefinanceinstitute.com/assets/budget-600x362.jpeg 600w, //cdn.corporatefinanceinstitute.com/assets/budget-498x300.jpeg 498w, //cdn.corporatefinanceinstitute.com/assets/budget.jpeg 1037w"
                sizes="(max-width: 1024px) 100vw, 1024px"
                data-src=""
                data-srcset="//cdn.corporatefinanceinstitute.com/assets/budget-1024x617.jpeg 1024w, //cdn.corporatefinanceinstitute.com/assets/budget-300x181.jpeg 300w, //cdn.corporatefinanceinstitute.com/assets/budget-768x463.jpeg 768w, //cdn.corporatefinanceinstitute.com/assets/budget-600x362.jpeg 600w, //cdn.corporatefinanceinstitute.com/assets/budget-498x300.jpeg 498w, //cdn.corporatefinanceinstitute.com/assets/budget.jpeg 1037w"
                data-sizes="(max-width: 1024px) 100vw, 1024px"
              ></img>
            </Box>
          )}
        </Box>
      </Layout>
    );
  }
}

const mapStateToProps = (state: MainStateType )=> {
  console.log(state);
  return {
    isLoading: state.appReducer.isLoading,
    user: state.userReducer.user,
  };
};

export default connect(mapStateToProps)(Main);
