import React, { useEffect, useState } from "react";
import { Box, Button, TextField, makeStyles } from "@material-ui/core";
import { connect } from "react-redux";
import { Formik, Form } from "formik";
import { createEntry, getEntries } from '../../modules/entries/entriesOperation'
import FormField from "../Form/FromField"
import { Route, Link } from "react-router-dom"
import EntriesItems from './EntriesItems'

const useStyles = makeStyles((theme) => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 200,
  },
}));

// interface BanksListPropsType {
//   get: any;
//   isLoading: boolean;
//   banks: Array<BankType>;
//   createBank:(bank: BankType) => Promise<boolean>;
//   updateBank: (bank: BankType) => Promise<boolean>;
//   deleteBank: (_id: string) => Promise<boolean>;
// }

const Entries: React.FC<{}> = ({ user, createEntry, getEntries }) => {
  const [date, setDate] = useState('2017-05-24')
  const classes = useStyles();

  // useEffect(() => {
  //   get();
  // }, [get]);

  // if (isLoading) {
  //   return <div>Loading ...</div>;
  // }

  function handeleChange(e) {
    console.log(e.target.value)
    setDate(e.target.value)
  }

  return (
    <Box>
       <h4>Entries</h4>
       <Box>
                <Formik
                    initialValues={{
                        catagorie: "",
                        amount: ""
                    }}
                    onSubmit={async (values, actions) => {


                        console.log('REsult', values)

                    }}
                >   
                    {(props) => (
                    <Form>
                      <Link to={`/entry/userId:${user.id}&date:${date}`}>
                        <Box>

                        <TextField
                            id="date"
                            label="Birthday"
                            onChange={handeleChange}
                            value={date}
                            type="date"
                            className={classes.textField}
                            InputLabelProps={{
                            shrink: true,
                          }}
                        />
                        </Box>
                        </Link>
                        <Box>
                            <FormField
                                {...props}
                                name="amount"
                                placeholder="Spent amount $"
                                label="amount"
                            />
                        </Box>
                        <Box>
                            <FormField
                                {...props}
                                name="catagorie"
                                placeholder="Catagory"
                                label="catagorie"
                            />
                        </Box>
                        <Button type="submit">
                              Create Entry
                        </Button>    
                    </Form>
                    )}
                </Formik>
            </Box>
    <Box>
      <Route path={'/entry/:userId&date'} component={EntriesItems} />
    </Box>
    </Box>
  );
};
const mapStateToProps = (state) => {
  return {
    user: state.userReducer.data,
    //isLoading: state.banksReducer.isLoading,
  };
};

const mapDispatchToState = {
  createEntry,
  getEntries
};


export default connect(mapStateToProps, mapDispatchToState)(Entries);
